import React from 'react';
import axios from 'axios';

class UserSignupPage extends React.Component {   //Bu class component'tır./Reactta 2 tip component vardır. Biri appjs'deki functional component. Diğeri clas comp./Functinal comp. için stateless denir yani içinde durum yok.
    state = {
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null
    }

    onChange = event => {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        });
    };

    onClickSignup = event => {
        event.preventDefault();
        const {username, displayName, password} = this.state;
        const body = {
            username,
            displayName,
            password
        }
        axios.post('/api/1.0/users', body);
    }

    render() {
        return (
            <div className={"container"}>
                <form>
                    <h1 className={"text-center"}>Sign Up</h1>
                    <div className="form-group">
                        <label>User Name</label>
                        <input className="form-control" name="username" onChange={this.onChange}/>
                    </div>
                    <div className="form-group">
                        <label>Display Name</label>
                        <input className="form-control" name="displayName" onChange={this.onChange}/>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input className="form-control" name="password" type="password" onChange={this.onChange}/>
                    </div>
                    <div className="form-group">
                        <label>Password Repeat</label>
                        <input className="form-control" name="passwordRepeat" type="password" onChange={this.onChange}/>
                    </div>
                    <div className="text-center">
                        <button className="btn btn-primary" onClick={this.onClickSignup}>
                            <style> padding: 100px </style>
                            Sing Up
                        </button>
                    </div>
                </form>
            </div>
        );
    }
}
export default UserSignupPage;
