import React from "react";

class UserSignupPage extends React.Component {   //Bu class component'tır.
//Reactta 2 tip component vardır. Biri appjs'deki functional component. Diğeri clas comp.
//Functinal comp. için stateless denir yani içinde durum yok.
    state = {
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null
    }
    onChangeUsername = event => {
        this.setState({
            username: event.target.value
        })
    }
    onChangeDisplayName = event => {
        this.setState({
            displayName: event.target.value
        })
    }
    onChangePassword = event => {
        this.setState({
            password: event.target.value
        })
    }
    onChangePasswordRepeat = event => {
        this.setState({
            passwordRepeat: event.target.value
        })
    }
    render() {
        return (
            <form>
                <h1>Sign Up</h1>
                <div>
                    <label>User Name</label>
                    <input name="username" onChange={this.onChangeUsername}/>
                </div>
                <div>
                    <label>Display Name</label>
                    <input name="displayName" onChange={this.onChangeDisplayName}/>
                </div>
                <div>
                    <label>Password</label>
                    <input name="password "type="password" onChange={this.onChangePassword}/>
                </div>
                <div>
                    <label>Password Repeat</label>
                    <input name="passwordRepeat" type="password" onChange={this.onChangePasswordRepeat}/>;
                </div>
                <button>Sing Up</button>
            </form>

        );
    }

}

export default UserSignupPage;
