import React from 'react';
import axios from 'axios';

class UserSignupPage extends React.Component {   //Bu class component'tır./Reactta 2 tip component vardır. Biri appjs'deki functional component. Diğeri clas comp./Functinal comp. için stateless denir yani içinde durum yok.
    state = {
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null
    }

    onChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    };

    onClickSignup = event => {
        event.preventDefault();
        const {username, displayName, password} = this.state;
        const body = {
            username,
            displayName,
            password
        }
        axios.post('http://localhost:9090/api/1.0/users', body);s
    }

    render() {
        return (
            <form>
                <h1>Sign Up</h1>
                <div>
                    <label>User Name</label>
                    <input name="username" onChange={this.onChange}/>
                </div>
                <div>
                    <label>Display Name</label>
                    <input name="displayName" onChange={this.onChange}/>
                </div>
                <div>
                    <label>Password</label>
                    <input name="password" type="password" onChange={this.onChange}/>
                </div>
                <div>
                    <label>Password Repeat</label>
                    <input name="passwordRepeat" type="password" onChange={this.onChange}/>;
                </div>
                <button onClick={this.onClickSignup}>Sing Up</button>
            </form>
        );
    }
}

export default UserSignupPage;
